function readCookie(n) {
	var cookiecontent = new String();
	if(document.cookie.length > 0) {
		var cookiename = n + '=';
		var cookiebegin = document.cookie.indexOf(cookiename);
		var cookieend = 0;
		if(cookiebegin > -1) {
			cookiebegin += cookiename.length;
			cookieend = document.cookie.indexOf(";", cookiebegin);
			if(cookieend < cookiebegin) {
				cookieend = document.cookie.length;
			}
			cookiecontent = document.cookie.substring(cookiebegin, cookieend);
			return unescape(cookiecontent);
		}
	}
	return false;
}
function getParam(variable){    
     var query = window.location.search.substring(1);     
     var vars = query.split("&");    
      for (var i=0;i<vars.length;i++) {      
            var pair = vars[i].split("=");     
            if(pair[0] == variable){return pair[1];}  
       }       return(false);  
}

function addButtons() {


	var icon = 'data:image/png;base64,';
	var clipconverterpath = 'http://www.clipconverter.cc/?ref=addon&version=132&browser=chrome&url=' + encodeURIComponent(document.URL);
	
	if(document.getElementById('watch-like-dislike-buttons')) {
		var div_embed = document.getElementById('watch-like-dislike-buttons');
		var buttonclass = "yt-uix-button yt-uix-button-text yt-uix-tooltip";
		var spanclass="";
	} else if(document.getElementById('watch-headline-user-info')) {
		var div_embed = document.getElementById('watch-headline-user-info');
		var buttonclass = "yt-uix-button yt-uix-button-default yt-uix-tooltip";
		var spanclass="yt-uix-button-group";
	}

	var title = '';
	var urlmap = '';
	var cookie = '';
	var target= '_blank';


	var result = /"url_encoded_fmt_stream_map": "(.*?)"/.exec(document.body.innerHTML);
	if(result) {
		urlmap = (result[1]);
		var result2 = /"adaptive_fmts": "(.*?)"/.exec(document.body.innerHTML);
		if(result2)
			urlmap +=  "," + (result2[1]);

		title = (document.title);
		cookie = "VISITOR_INFO1_LIVE=" + readCookie("VISITOR_INFO1_LIVE");

		if(readCookie("goojf")) {
			cookie += "; goojf=" + readCookie("goojf");
		}
	}
	
	if(getParam('clipconverter')=='autostart' || getParam('clipconverter')=='frame') {
		target='_parent';
	}
	
	var form = '<form id="clipconverter" name="clipconverter" action="' + clipconverterpath + '" target="' + target + '" method="POST"><input type="hidden" name="urlmap" value="' + urlmap + '"><input type="hidden" name="title" value="' + title + '"><input type="hidden" name="format" id="format" value=""><input type="hidden" name="cookie" value="' + cookie + '"></form>';
	
	if(document.getElementById('header')) {
		document.getElementById('header').innerHTML += form;
	}
	else if(document.getElementById('footer')) {
		document.getElementById('footer').innerHTML += form;
	}		
	else if(div_embed) {
		div_embed.innerHTML += form;
	}

	if(div_embed) {
		div_embed.innerHTML += ' <span class="' + spanclass + '"><a href="javascript:(function(){ document.getElementById(\'format\').value=\'\'; document.getElementById(\'clipconverter\').submit();  })();"><button class="start ' + buttonclass + '" type="button" title="Record video with ClipConverter"><img alt="" class="" style="" src="' + icon + '"> <span class="yt-uix-button-content"><strong>ClipConverter</strong></span></button></a>' + '<a href="javascript:(function(){ document.getElementById(\'format\').value=\'mp3\'; document.getElementById(\'clipconverter\').submit(); })();"><button class="' + buttonclass + '" type="button" title="Record audio as MP3">MP3</span></button></a>' + '<a href="javascript:(function(){ document.getElementById(\'format\').value=\'m4a\'; document.getElementById(\'clipconverter\').submit(); })();"><button class="' + buttonclass + '" type="button" title="Record audio as M4A">M4A</span></button></a>' + '<a href="javascript:(function(){ document.getElementById(\'format\').value=\'mp4\'; document.getElementById(\'clipconverter\').submit(); })();"><button class="end ' + buttonclass + '" type="button" title="Record video as MP4">MP4</span></button></a></span>';
	}
	



}(function() {

		setInterval(function(){
			if(!document.getElementById('clipconverter')) {
				addButtons();
			}
		}, 1000);
	

	addButtons();

	
	if(getParam('clipconverter')=='autostart') {
		document.getElementById('clipconverter').submit();
	}

})();
